import sqlite3import osdef database_fetch(plastic_type, output_dir):    # Connect to the SQLite database    database_path = os.path.join(os.getcwd(), "PlasticEnzymes.db")  # Replace "your_database.db" with your database name    conn = sqlite3.connect(database_path)    cursor = conn.cursor()    # SQL query    query = f"""    SELECT Main_copy.Genus, Main_copy.Species, Main_copy.Strain, Main_copy.Enzyme, Sequences.SEQUENCE    FROM Main_copy    JOIN Sequences ON Main_copy.Sequences_id = Sequences.Seq_Pk    JOIN Plastic ON Main_copy.Plastic_id = Plastic.Plastic_id    WHERE Plastic.PLASTIC = ?;    """    # Execute the query    cursor.execute(query, (plastic_type,))    # Fetch all rows from the executed SQL query    rows = cursor.fetchall()    # Close the connection    conn.close()    # Save the fetched data in FASTA format    output_file_path = os.path.join(output_dir, f"{plastic_type}_sequences.fasta")    with open(output_file_path, 'w') as output_file:        for row in rows:            # Prepare the first part of the header (row[0] and row[1]), replace None with an empty string            first_part = ' '.join([str(item).replace(' ', ' ') if item is not None else '' for item in row[:2]])                        # Prepare the second part of the header (row[2] and row[3]), skip None values            second_part = '|'.join([str(item).replace(' ', ' ') for item in row[2:4] if item is not None])                # Combine the two parts of the header            header = f"{first_part}|{second_part}"                # Prepare the sequence            sequence = row[4].replace(' ', '').replace('\t', '').replace('\n', '')                output_file.write(f">{header}\n{sequence}\n")